import React, { Component } from 'react';
import { Redirect } from "react-router-dom";
import { FormGroup } from 'reactstrap';

class CadastroProduto extends Component{
    constructor(){
        super();

        this.state = {
            produto: {
                codigo: 0,
                nomeDoLivro: "",
                autor: "",
                quantidadeEstoque: 0,
                vendidos: 0,
                status: ""
            },
            Redirect: false,
        }
    }

    render () {
        const {Redirect} = this.state;
        if(Redirect) {
            return <Redirect to="/" />
        }else{
            return(
                <form onSubmit={this.handleSubmit}>  
                    <legend>Cadastro de Produtos</legend>
                    <div>
                        <label>Nome do Livro: </label>
                        <input 
                        type="text" 
                        name="nome" 
                        id="nome" 
                        placeholder="Insira o nome do livro"
                        value={this.state.produto.codigo}
                        onChange={this.handleInputChange}/>

                    </div>
                <row form>
                    <col md={4}>
                        <formgroup>
                        
                        </formgroup>
                    </col> 
                    <col md={3}>
                        <formgroup>
                        <label>Autor: </label>
                        <input type="nome" name="nome" id="Formnome" placeholder="Insira o nome do Autor" />
                        </formgroup>
                    </col>
                    </row>
                        
                    <row form>
                    <col md={1}>    
                        <formgroup>
                        <label>Quantidade:</label>
                        <input type="quantidade" name="quantidade" id="Formqtde" placeholder="Insira a quantidade" />
                        </formgroup>
                    </col>
                    </row>
                            
                <button>Salvar</button>
                </form>
            )
        }
    }
}
export default CadastroProduto;
