import React, {Component} from 'react';
import { Col, Row, Button, Form, FormGroup, Label, Input } from 'reactstrap';
import {Redirect} from 'react-router-dom' 


class CadastroProduto extends Component{
    constructor(){
        super();

        this.state = {
          produtos:  {
                
                codigo: 0,
                nomeDoLivro: "",
                autor: "",
                quantidadeEstoque: 0,
                vendidos: 0,
                status: ""              
               
              },
              Redirect: false,
        }        
    }

    render() {
        const {Redirect} = this.state;
        if(Redirect) {
            return <Redirect to = "/" />           
        }else{
            return (
          
            <Form>  
                <Row form>
                <Col md={4}>
                    <FormGroup>
                    <Label>Nome do Livro: </Label>
                    <Input type="codigo" name="codigo" id="Formcodigo" placeholder="Insira o nome do livro" />
                    </FormGroup>
                </Col> 
                <Col md={3}>
                    <FormGroup>
                    <Label>Autor: </Label>
                    <Input type="nome" name="nome" id="Formnome" placeholder="Insira o nome do Autor" />
                    </FormGroup>
                </Col>
                </Row>
    
             
                
                <Row form>
                <Col md={1}>    
                    <FormGroup>
                    <Label>Quantidade:</Label>
                    <Input type="quantidade" name="quantidade" id="Formqtde" placeholder="Insira a quantidade" />
                    </FormGroup>
                </Col>
                </Row>
                          
            <Button>Salvar</Button>
          </Form>
                
            );
        }
    }
}







export default CadastroProduto;
