import React, { Component } from 'react';
import { Redirect } from "react-router-dom";
import { FormGroup } from 'reactstrap';

class CadastroProduto extends Component{
    constructor(){
        super();

        this.state = {
            produto: {
                codigo: 0,
                nomeDoLivro: "",
                autor: "",
                quantidadeEstoque: 0,
               
            },
            Redirect: false,
        }
    }

    render () {
        const {Redirect} = this.state;
        if(Redirect) {
            return <Redirect to="/" />
        }else{
            return(
                <form onSubmit={this.handleSubmit}>  
                    <legend>Cadastro de Produtos</legend>
                    <div>
                        <label>Código: </label>
                        <input 
                        type="number" 
                        name="nome" 
                        id="nome" 
                        placeholder="Insira código"
                        value={this.state.produto.codigo}
                        onChange={this.handleInputChange}/>
                    </div>
                    <div>
                        <label>Nome do Livro: </label>
                        <input 
                        type="text" 
                        name="nome" 
                        id="nome" 
                        placeholder="Insira nome do livro"
                        value={this.state.produto.nomeDoLivro}
                        onChange={this.handleInputChange}/>
                    </div>
                    <div>
                        <label>Autor: </label>
                        <input 
                        type="text" 
                        name="nome" 
                        id="nome" 
                        placeholder="Insira nome do autor"
                        value={this.state.produto.autor}
                        onChange={this.handleInputChange}/>
                    </div>
                    <div>
                        <label>Quantidade: </label>
                        <input 
                        type="number" 
                        name="nome" 
                        id="nome" 
                        placeholder="Insira quantidade"
                        value={this.state.produto.quantidadeEstoque}
                        onChange={this.handleInputChange}/>
                    </div>

               
                            
                <button>Salvar</button>
                </form>
            )
        }
    }
}
export default CadastroProduto;
